import logo from './logo.svg';
import './App.css';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div>
      <h1>Financial Data</h1>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Income</th>
            <th>Outcome</th>
            <th>Clear</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.date}</td>
              <td>{row.income}</td>
              <td>{row.outcome}</td>
              <td>{row.clear}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
