+const express = require('express');
+const cors = require('cors');
+const dotenv = require('dotenv');
+const { Client } = require('pg');
+
+dotenv.config();
+
+const app = express();
+const PORT = process.env.PORT || 5000;
+
+app.use(cors());
+app.use(express.json());
+
+const client = new Client({
+    connectionString: process.env.DB_URL,
+    ssl: { rejectUnauthorized: false }
+});
+
+app.get('/', (req, res) => {
+    res.send('API is running...');
+});
+
+app.listen(PORT, () => {
+    console.log(`Server is running on http://localhost:${PORT}`);
+});
+
+client.query('SELECT NOW()', (err, res) => {
+    if (err) {
+        console.error('‚ùå Database connection failed:', err.stack);
+    } else {
+        console.log('‚úÖ Connected to NeonDB! Current time:', res.rows[0].now);
+    }
+});
+
+client.connect()
+    .then(() => {
+        console.log('‚úÖ Connected to NeonDB!');
+        return client.query('SELECT NOW()');
+    })
+    .then(res => {
+        console.log('üìÖ Current time in DB:', res.rows[0].now);
+    })
+    .catch(err => {
+        console.error('‚ùå Database connection failed:', err.stack);
+    });
+
+    app.get('/api/data', async (req, res) => {
+        try {
+            const result = await client.query('SELECT * FROM financial_data ORDER BY date');
+            res.json(result.rows);
+        } catch (error) {
+            console.error('‚ùå Error fetching data:', error);
+            res.status(500).json({ error: 'Internal server error' });
+        }
+    });
+
